import{getType}from"./dataType.js";import{typeErrorException}from"./exceptions.js";export function clearNecessarySpaceOnBacktick(a){if("string"!==getType(a))throw new Error(`parameter str must be typeof string, ${getType(a)} given`);let b=a.split("\n"),c="";for(let d of b)c+=`${d.trimStart()}\n`;return c}export function alphabets({except:a}){var b=String.fromCharCode;if("string"!==getType(a)||"array"!==getType(a))throw typeErrorException(a,"string | array");let c="",d=[];d="string"===getType(a)?a.split(""):a;for(let e,f=97;f<=122;f++)e=b(f),d.includes(e)||(c+=e);return c}